from microbit import *#write your program:suffix = "\r\n" Status = 'Retrieving Data from Serial Port...'+suffix    while(1):             uart.write(Status)    if uart.any():        uart.write('Data Retrived:'+suffix)        incoming = str(uart.readall(), "UTF-8")        if incoming == 'h':                                    uart.write("Match!"+suffix)        else:            uart.write(incoming+suffix)        sleep(2000)    sleep(500)